def read_file_to_one_big_string(filename):
    with open(filename) as f:
        return f.read()


day_number = 6
part1_test_solution = 7
part2_test_solution = 19
test_input = """
mjqjpqmgbljsphdztnvjfqwrcgsmlb
""".strip(
    "\n"
)


input_raw = ""
# read_file_to_one_big_string(f'2022/data/day{day_number:02d}/input.txt')


def part1(input_raw: str):
    marker_len = 4
    length = len(input_raw)
    for i in range(length - marker_len):
        fourchars = input_raw[i : i + marker_len]
        s = set(fourchars)
        if len(s) == marker_len:
            return i + marker_len
    return -1


def part2(input_raw: str):
    marker_len = 14
    length = len(input_raw)
    for i in range(length - marker_len):
        fourchars = input_raw[i : i + marker_len]
        s = set(fourchars)
        if len(s) == marker_len:
            return i + marker_len
    return -1


if part1_test_solution is None:
    print(f"Part 1 Test: {part1(test_input)}")
    quit()

assert part1(test_input) == part1_test_solution
print(f"Part 1: {part1(input_raw)}")

if part2_test_solution is None:
    print(f"Part 2 Test: {part2(test_input)}")
    quit()

assert part2(test_input) == part2_test_solution
print(f"Part 2: {part2(input_raw)}")
